CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);
CREATE TABLE "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "auth_user_groups" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "auth_user_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "action_time" datetime NOT NULL);
CREATE TABLE "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);
CREATE TABLE "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);
CREATE TABLE "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(150) NOT NULL UNIQUE);
CREATE TABLE "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "username" varchar(150) NOT NULL UNIQUE, "last_name" varchar(150) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "first_name" varchar(150) NOT NULL);
CREATE TABLE "django_session" ("session_key" varchar(40) NOT NULL PRIMARY KEY, "session_data" text NOT NULL, "expire_date" datetime NOT NULL);
CREATE TABLE "login_emailcode" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "email" varchar(254) NOT NULL, "code" varchar(6) NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE "login_userprofile" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "email" varchar(254) NOT NULL UNIQUE, "user_id" integer NOT NULL UNIQUE REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "api_usage_statistics" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "api_name" varchar(100) NOT NULL, "date" date NOT NULL, "total_requests" integer NOT NULL, "successful_requests" integer NOT NULL, "failed_requests" integer NOT NULL, "total_execution_time" real NOT NULL, "total_request_size" bigint NOT NULL, "total_response_size" bigint NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "api_access_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "ip_address" char(39) NULL, "user_agent" text NULL, "api_name" varchar(100) NOT NULL, "api_path" varchar(200) NOT NULL, "http_method" varchar(10) NOT NULL, "request_data" text NULL CHECK ((JSON_VALID("request_data") OR "request_data" IS NULL)), "request_params" text NULL CHECK ((JSON_VALID("request_params") OR "request_params" IS NULL)), "response_status" integer NULL, "response_data" text NULL CHECK ((JSON_VALID("response_data") OR "response_data" IS NULL)), "error_message" text NULL, "execution_time" real NULL, "request_size" integer NULL, "response_size" integer NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "is_success" bool NOT NULL, "api_version" varchar(20) NOT NULL, "user_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "ai_assistant_config" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE, "system_prompt" text NOT NULL, "model_type" varchar(20) NOT NULL, "model_name" varchar(50) NOT NULL, "max_tokens" integer NOT NULL, "temperature" real NOT NULL, "max_history" integer NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL);
CREATE TABLE "ai_assistant_conversation" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" varchar(100) NOT NULL, "session_id" varchar(100) NOT NULL UNIQUE, "title" varchar(200) NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "is_active" bool NOT NULL);
CREATE TABLE "ai_assistant_tool" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE, "description" text NOT NULL, "function_name" varchar(100) NOT NULL, "parameters" text NOT NULL CHECK ((JSON_VALID("parameters") OR "parameters" IS NULL)), "is_active" bool NOT NULL);
CREATE TABLE "ai_assistant_message" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "role" varchar(20) NOT NULL, "content" text NOT NULL, "created_at" datetime NOT NULL, "tokens_used" integer NOT NULL, "conversation_id" bigint NOT NULL REFERENCES "ai_assistant_conversation" ("id") DEFERRABLE INITIALLY DEFERRED);
